mapper.insert:
insert into gradesandcertificates (GradeAndCertificateID, student_id, 
  course_id, FinalGrade, CertificateURL
  )
values (#{gradeAndCertificateID,jdbcType=INTEGER}, #{student_id,jdbcType=INTEGER}, 
  #{course_id,jdbcType=INTEGER}, #{finalGrade,jdbcType=INTEGER}, #{certificateURL,jdbcType=VARCHAR}
  )

mapper.update:
update gradesandcertificates
set student_id = #{student_id,jdbcType=INTEGER},
  course_id = #{course_id,jdbcType=INTEGER},
  FinalGrade = #{finalGrade,jdbcType=INTEGER},
  CertificateURL = #{certificateURL,jdbcType=VARCHAR}
where GradeAndCertificateID = #{gradeAndCertificateID,jdbcType=INTEGER}

mapper.select:
select GradeAndCertificateID, student_id, course_id, FinalGrade, CertificateURL
from gradesandcertificates
where GradeAndCertificateID = #{gradeAndCertificateID,jdbcType=INTEGER}
