mapper.update:
update onlinelearning
set student_id = #{student_id,jdbcType=INTEGER},
  course_id = #{course_id,jdbcType=INTEGER},
  video_url = #{video_url,jdbcType=VARCHAR},
  quiz_Results = #{quiz_Results,jdbcType=INTEGER}
where Learning_id = #{learning_id,jdbcType=INTEGER}

mapper.select:
select Learning_id, student_id, course_id, video_url, quiz_Results
from onlinelearning
where Learning_id = #{learning_id,jdbcType=INTEGER}
